mapper.insert:
 	INSERT INTO RSTAGE(
		rstage_name,
		rstage_theme)
	VALUES(
		#{rstage_name:VARCHAR},
		#{rstage_theme:VARCHAR}
	);

mapper.update:
   	UPDATE RSTAGE
	SET
		rstage_name 		= #{rstage_name:VARCHAR},
		rstage_theme 		= #{rstage_theme:VARCHAR},
		rstage_del_check 	= #{rstage_del_check:BOOLEAN}
	WHERE rstage_no 		= #{rstage_no:INTEGER};
